##KEYBOARD AND SCREEN HELPERS
def updateKeypad():
	global keypad
	##FIRST ROW
	if keyboard.is_pressed('1'):
		keypad['K1'] = 1
	if keyboard.is_pressed('2'):
		keypad['K2'] = 1
	if keyboard.is_pressed('3'):
		keypad['K3'] = 1
	if keyboard.is_pressed('4'):
		keypad['Kc'] = 1
	##SECOND ROW
	if keyboard.is_pressed('q'):
		keypad['K4'] = 1
	if keyboard.is_pressed('w'):
		keypad['K5'] = 1
	if keyboard.is_pressed('e'):
		keypad['K6'] = 1
	if keyboard.is_pressed('r'):
		keypad['Kd'] = 1
	##THIRD ROW
	if keyboard.is_pressed('a'):
		keypad['K7'] = 1
	if keyboard.is_pressed('s'):
		keypad['K8'] = 1
	if keyboard.is_pressed('d'):
		keypad['K9'] = 1
	if keyboard.is_pressed('f'):
		keypad['Ke'] = 1
	##FORTH ROW
	if keyboard.is_pressed('z'):
		keypad['Ka'] = 1
	if keyboard.is_pressed('x'):
		keypad['K0'] = 1
	if keyboard.is_pressed('c'):
		keypad['Kb'] = 1
	if keyboard.is_pressed('v'):
		keypad['Kf'] = 1
	else: 
		pass

def waitForKeypress():
	while True:
	##FIRST ROW
		if keyboard.is_pressed('1'):
			return(0x1)
		if keyboard.is_pressed('2'):
			return(0x2)
		if keyboard.is_pressed('3'):
			return(0x3)
		if keyboard.is_pressed('4'):
			return(0xc)
		##SECOND ROW
		if keyboard.is_pressed('q'):
			return(0x4)
		if keyboard.is_pressed('w'):
			return(0x5)
		if keyboard.is_pressed('e'):
			return(0x6)
		if keyboard.is_pressed('r'):
			return(0xd)
		##THIRD ROW
		if keyboard.is_pressed('a'):
			return(0x7)
		if keyboard.is_pressed('s'):
			return(0x8)
		if keyboard.is_pressed('d'):
			return(0x9)
		if keyboard.is_pressed('f'):
			return(0xe)
		##FORTH ROW
		if keyboard.is_pressed('z'):
			return(0xa)
		if keyboard.is_pressed('x'):
			return(0x0)
		if keyboard.is_pressed('c'):
			return(0xb)
		if keyboard.is_pressed('v'):
			return(0xf)
			
def resetKeypad():
	global keypad
	keypad=dict((k, 0) for k in keypad)

##Function to load the binary at 0+offset in memory
def load_binary(filename, offset):
	global memory
	
	counter = offset

	file = open(filename, 'rb')
	byte = file.read(1)
	while byte:
			i = int.from_bytes(byte, byteorder='big')
			byte = file.read(1)
			memory[counter] = i
			counter += 1
	file.close()


